import sys, json
routes = json.loads(sys.argv[1])
lowest_hop_for_trace = int(sys.argv[2])
lowest_path_trace = int(sys.argv[3])
if not isinstance(routes, list):
  routes = [routes]
routes.sort(key=lambda x: x['pathId'])
offending_trace = routes[lowest_path_trace - 1]
offending_hop = {}
for trace in offending_trace['hops']:
  if trace['hop'] == lowest_hop_for_trace:
    offending_hop = json.dumps(trace)
    break
else:
  raise Exception("Hop isn't in list, this is likely a bug in the workflow or in thousand eyes")

import sys, json
if sys.argv[1].startswith('['):
  affectedAgents = json.loads(sys.argv[1])
else:
  affectedAgents = [sys.argv[1]]
if sys.argv[2].startswith('['):
  totalAgents = json.loads(sys.argv[2])
else:
  totalAgents = [sys.argv[2]]
if len(affectedAgents) == len(totalAgents):
  severity_level = "Major"
else:
  severity_level = "Minor"
